// export default SignIn;

import React, { useState } from "react";
import axios from "axios";

const SignIn = () => {
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [manufactureData, setManufactureData] = useState(null);
    const [productsData, setProductsData] = useState(null);
    const [message, setMessage] = useState("");

    const handleLogin = async () => {
        try {
            const response = await axios.post("http://localhost:3001/login", {
                username: username,
                password: password,
            });
            const data = response.data;
            if (data.success) {
                setManufactureData(data.manufacture);
                setProductsData(data.products);
                setMessage("");
            } else {
                setMessage(data.message);
            }
        } catch (error) {
            console.error("Error during login:", error);
            setMessage("Internal server error");
        }
    };

    return (
        <div>
            <h2>Manufacture Login</h2>
            <div>
                <label>Username:</label>
                <input
                    type="text"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                />
            </div>
            <div>
                <label>Password:</label>
                <input
                    type="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                />
            </div>
            <button onClick={handleLogin}>Login</button>
            {message && <p>{message}</p>}
            {manufactureData && (
                <div>
                    <h3>Manufacture Details:</h3>
                    <p>Company Name: {manufactureData.companyName}</p>
                    <p>State Location: {manufactureData.stateLocation}</p>
                    {/* Add more fields as needed */}
                </div>
            )}
            {productsData && (
                <div>
                    <h3>Products:</h3>
                    <ul>
                        {productsData.map((product) => (
                            <li key={product._id}>
                                Product ID: {product.productId}, Product Name: {product.productName}
                            </li>
                        ))}
                    </ul>
                </div>
            )}
        </div>
    );
};

export default Manufacture;
