import { useState } from "react";
import axios from 'axios';

const Signin = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [message, setMessage] = useState('');
    const [manufactureData, setManufactureData] = useState(null);
    const [productsData, setProductsData] = useState([]);

    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('/Signin', { username, password });
            const { success, message, manufacture, products } = response.data;
            if (success) {
                setMessage(message);
                setManufactureData(manufacture);
                setProductsData(products);
            } else {
                setMessage(message);
                setManufactureData(null);
                setProductsData([]);
            }
        } catch (error) {
            console.error("Error during login:", error);
            setMessage("Internal server error");
        }
    };

    return (
        <div>
            <h2>Sign In</h2>
            <form onSubmit={handleLogin}>
                <div>
                    <label>Username:</label>
                    <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} />
                </div>
                <div>
                    <label>Password:</label>
                    <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
                </div>
                <button type="submit">Login</button>
            </form>
            {message && <p>{message}</p>}
            {manufactureData && (
                <div>
                    <h3>Manufacture Details:</h3>
                    <p>Company Name: {manufactureData.companyName}</p>
                    <p>State Location: {manufactureData.stateLocation}</p>
                    <p>PAN Number: {manufactureData.panNumber}</p>
                    <p>Number of Entities: {manufactureData.numOfEntity}</p>
                    <p>Government Bodies: {manufactureData.govtBodies}</p>
                    <p>GST Number: {manufactureData.gstNumber}</p>
                </div>
            )}
            {productsData.length > 0 && (
                <div>
                    <h3>Products:</h3>
                    <ul>
                        {productsData.map((product) => (
                            <li key={product._id}>
                                Product ID: {product.productId}<br />
                                Product Name: {product.productName}<br />
                                Product Brand: {product.productBrand}<br />
                            </li>
                        ))}
                    </ul>
                </div>
            )}
        </div>
    );
};

export default Signin;
